import { useGetUserQuery, useCreateUserMutation } from "../src/store/features/api/apiSlice";

const App = () => {
  const [createUserHandler, {isLoading}] = useCreateUserMutation();
  const { isLoading: isP, data } = useGetUserQuery();

  console.log("post loading ::", isLoading);
  console.log("post data ::", data);

  // create post handler
  const createPostHandler = (data) => {
    console.log("createPostHandler", data);
  };

  return (
    <div className="App">
      <h2>Awesome RTK Query</h2>

      <div>
        <button
          onClick={() =>
            createPostHandler({
              name: "Md Mohimenol Islam Munna",
              job: "React Developer",
            })
          }
        >
          Create User
        </button>
      </div>
    </div>
  );
};

export default App;
