import "./App.css";
import {
  useGetUserQuery,
  useCreateUserMutation,
} from "../src/store/features/api/apiSlice";

const App = () => {
  const [createUserMutationHandler, { isLoading }] = useCreateUserMutation();
  const { isLoading: isPostLoading, data } = useGetUserQuery();

  console.log("post isPostLoading ::", isPostLoading);
  console.log("post data ::", data);

  // create post handler
  const createUserHandler = async (data) => {
    console.log("createPostHandler", data);

    try{

      createUserMutationHandler(data);
    } catch(err){
      console.log("")
    }
  };

  return (
    <div className="App">
      <h2>Awesome RTK Query</h2>

      <div>
        <button
          onClick={() =>
            createUserHandler({
              name: "Md Mohimenol Islam Munna",
              job: "React Developer",
            })
          }
        >
          Create User
        </button>
      </div>
    </div>
  );
};

export default App;
